function getFinalFloor($input)
{
    return substr_count($input, "(") - substr_count($input, ")");
}

function getBasementIndex($input)
{
    $floor = 0;
    $i = 0;
    while ($floor >= 0) {
        if ($input[$i] == "(") {
            $floor += 1;
        } else {
            $floor -= 1;
        }
        $i++;
    }
    return $i;
}

